settings.custom_code:
{% assign gid_list = product.metafields.custom.works_well_with %}
{% assign product_ids_array = '' %}

<!-- Debugging: Output GID list -->
<p>GID List: {{ gid_list }}</p>

{% assign gid_list = gid_list | replace: '\"', '' %}
{% assign gid_list = gid_list | replace: '[', '' %}
{% assign gid_list = gid_list | replace: ']', '' %}
{% assign gid_list = gid_list | split: ',' %}

{% for gid in gid_list %}
  {% assign product_id = gid | split: '/' | last %}
  {% assign product_ids_array = product_ids_array | append: product_id | append: ',' %}
  <!-- Debugging: Output each extracted product ID -->
  <p>Extracted Product ID: {{ product_id }}</p>
{% endfor %}

{% assign product_ids_array = product_ids_array | split: ',' | where: 'size', '>', 0 %}

<!-- Debugging: Output the final array of product IDs -->
<p>Product IDs Array: {{ product_ids_array }}</p>

<section id="WorksWellWith" class="section-padding featured-collection featured-collection--grid collection-items collection-items--4 bg-secondary">
  <div class="wrapper">
    <div class="featured-collection__top text-center">
      <div class="section__header featured-collection__top__wrapper">
        <h2 class="section__heading h2">
          <p>WORKS WELL WITH</p><span class="red_dot"></span>
        </h2>
      </div>
    </div>

    <div class="featured-collection__container grid__container">
      <div class="grid grid--mobile">
        {% for product_id in product_ids_array %}
          {% assign related_product = all_products[product_id] %}
          <!-- Debugging: Output if product is found or not -->
          {% if related_product %}
            <div class="grid__item">
              <a href="{{ related_product.url }}" class="product-card">
                <div class="product-card__image-wrapper">
                  <img src="{{ related_product.featured_image | img_url: 'medium' }}" alt="{{ related_product.title }}" class="product-card__image">
                </div>
                <div class="product-card__details">
                  <p class="product-card__title">{{ related_product.title }}</p>
                  <p class="product-card__price">{{ related_product.price | money }}</p>
                </div>
              </a>
            </div>
          {% else %}
            <p>Product not found for ID: {{ product_id }}</p>
          {% endif %}
        {% endfor %}
      </div>
    </div>
  </div>
</section>
